// Parse OpenAI response and extract flashcards
const response = $input.first().json.message.content;

try {
  // Try to parse the JSON response
  let flashcards;

  // Clean the response to extract JSON
  const cleanedResponse = response.replace(/```json\n?|```\n?/g, '').trim();

  try {
    flashcards = JSON.parse(cleanedResponse);
  } catch (e) {
    // If direct parsing fails, try to extract JSON from the text
    const jsonMatch = cleanedResponse.match(/\[(.*?)\]/s);
    if (jsonMatch) {
      flashcards = JSON.parse('[' + jsonMatch[1] + ']');
    } else {
      throw new Error('Could not extract JSON from response');
    }
  }

  // Validate flashcards format
  if (!Array.isArray(flashcards)) {
    throw new Error('Response is not an array');
  }

  // Ensure all flashcards have required fields
  flashcards = flashcards.filter(card => 
    card.question && card.answer && 
    typeof card.question === 'string' && 
    typeof card.answer === 'string'
  );

  if (flashcards.length === 0) {
    throw new Error('No valid flashcards found');
  }

  return {
    success: true,
    flashcards: flashcards,
    count: flashcards.length,
    iteration: $('Initialize Data').item.json.iteration
  };

} catch (error) {
  console.error('Error parsing flashcards:', error);

  // Fallback: create sample flashcards
  const fallbackFlashcards = [
    {
      question: "What is the main topic of this session?",
      answer: "Based on the provided transcript content."
    },
    {
      question: "What are the key concepts discussed?", 
      answer: "The primary concepts covered in the session."
    }
  ];

  return {
    success: false,
    error: error.message,
    flashcards: fallbackFlashcards,
    count: fallbackFlashcards.length,
    iteration: $('Initialize Data').item.json.iteration,
    fallback: true
  };
}

// Parse quality analysis response
const response = $input.first().json.message.content;
const flashcardsData = $('Parse Flashcards').item.json;
const currentIteration = flashcardsData.iteration;
const maxIterations = $('Initialize Data').item.json.maxIterations;

try {
  // Clean and parse the analysis response
  const cleanedResponse = response.replace(/```json\n?|```\n?/g, '').trim();
  const analysis = JSON.parse(cleanedResponse);

  // Validate analysis structure
  if (!analysis.overallScore || !analysis.comprehensiveness || !analysis.complexity || !analysis.quality) {
    throw new Error('Invalid analysis format');
  }

  // Determine if improvement is needed
  const needsImprovement = (
    analysis.overallScore < 7.5 || 
    analysis.comprehensiveness < 6 || 
    analysis.complexity < 6 || 
    analysis.quality < 6
  ) && currentIteration < maxIterations;

  console.log(`Iteration ${currentIteration}: Overall Score ${analysis.overallScore}/10`);
  console.log(`Needs improvement: ${needsImprovement}`);

  return {
    analysis: analysis,
    flashcards: flashcardsData.flashcards,
    iteration: currentIteration,
    maxIterations: maxIterations,
    needsImprovement: needsImprovement,
    isComplete: !needsImprovement,
    transcript: $('Initialize Data').item.json.transcript,
    sessionId: $('Initialize Data').item.json.sessionId
  };

} catch (error) {
  console.error('Error parsing analysis:', error);

  // Fallback analysis
  const fallbackAnalysis = {
    comprehensiveness: 7,
    complexity: 7, 
    quality: 7,
    overallScore: 7.0,
    feedback: "Analysis parsing failed, using fallback scoring"
  };

  return {
    analysis: fallbackAnalysis,
    flashcards: flashcardsData.flashcards,
    iteration: currentIteration,
    maxIterations: maxIterations,
    needsImprovement: false,
    isComplete: true,
    transcript: $('Initialize Data').item.json.transcript,
    sessionId: $('Initialize Data').item.json.sessionId,
    error: error.message
  };
}

// Parse improved flashcards and prepare for next iteration
const response = $input.first().json.message.content;
const previousData = $('Decision Node').item.json;

try {
  // Parse the improved flashcards
  const cleanedResponse = response.replace(/```json\n?|```\n?/g, '').trim();
  let improvedFlashcards;

  try {
    improvedFlashcards = JSON.parse(cleanedResponse);
  } catch (e) {
    const jsonMatch = cleanedResponse.match(/\[(.*?)\]/s);
    if (jsonMatch) {
      improvedFlashcards = JSON.parse('[' + jsonMatch[1] + ']');
    } else {
      throw new Error('Could not extract JSON from improved response');
    }
  }

  // Validate improved flashcards
  if (!Array.isArray(improvedFlashcards)) {
    throw new Error('Improved response is not an array');
  }

  improvedFlashcards = improvedFlashcards.filter(card => 
    card.question && card.answer && 
    typeof card.question === 'string' && 
    typeof card.answer === 'string'
  );

  if (improvedFlashcards.length === 0) {
    throw new Error('No valid improved flashcards found');
  }

  console.log(`Iteration ${previousData.iteration}: Improved ${improvedFlashcards.length} flashcards`);

  return {
    flashcards: improvedFlashcards,
    iteration: previousData.iteration + 1,
    maxIterations: previousData.maxIterations,
    transcript: previousData.transcript,
    sessionId: previousData.sessionId,
    success: true,
    count: improvedFlashcards.length
  };

} catch (error) {
  console.error('Error parsing improved flashcards:', error);

  // Return previous flashcards if improvement fails
  return {
    flashcards: previousData.flashcards,
    iteration: previousData.iteration + 1, 
    maxIterations: previousData.maxIterations,
    transcript: previousData.transcript,
    sessionId: previousData.sessionId,
    success: false,
    error: error.message,
    count: previousData.flashcards.length
  };
}

// Final response preparation
const data = $input.first().json;
const isFromImprovement = $input.first().json.hasOwnProperty('analysis');

// Determine the source of final data
let finalFlashcards, finalIteration, analysisData;

if (isFromImprovement) {
  // Coming from the improvement loop
  finalFlashcards = data.flashcards;
  finalIteration = data.iteration;
  analysisData = data.analysis;
} else {
  // Coming directly from quality check (no improvement needed)
  finalFlashcards = data.flashcards;
  finalIteration = data.iteration;
  analysisData = data.analysis;
}

// Prepare comprehensive response
const response = {
  success: true,
  flashcards: finalFlashcards,
  metadata: {
    iterations: finalIteration,
    maxIterations: data.maxIterations || 5,
    totalGenerated: finalFlashcards.length,
    sessionId: data.sessionId,
    generatedAt: new Date().toISOString()
  }
};

// Add analysis data if available
if (analysisData) {
  response.metadata.analysis = {
    overallScore: analysisData.overallScore,
    comprehensiveness: analysisData.comprehensiveness,
    complexity: analysisData.complexity,
    quality: analysisData.quality,
    feedback: analysisData.feedback
  };
}

// Add fallback flag if present
if (data.fallback) {
  response.metadata.fallback = true;
}

// Add error information if present
if (data.error) {
  response.metadata.warnings = [data.error];
}

console.log(`ðŸŽ‰ Flashcard generation completed: ${finalFlashcards.length} cards in ${finalIteration} iterations`);

return response;
